# slice(x, 13)
series = 
product = 0
winning_series = 0

(0..(series.to_s.length-1)).each do |x|
	candidate = series.to_s.slice(x, 13)
	candidate_product = candidate.split(//).inject(1){ |total, y| total * y.to_i} # split numbers into array, multiply the numbers together
	if candidate_product > product
		puts "Found a winner in #{candidate} with #{candidate_product}"
		winning_series = candidate.to_i 
		product = candidate_product
	end
end

puts "The 13 adjacent numbers with the largest product are #{winning_series} with a product of #{product}"